<node CREATED="1120717351020" FOLDED="true" ID="Freemind_Link_1565653839" MODIFIED="1120717411878" TEXT="handle &quot;mixed&quot; PHP code">
<node COLOR="#cc6600" CREATED="1121810808743" ID="Freemind_Link_969861567" MODIFIED="1126818305370" TEXT="// ScriptReorganizer_Type::reformat()&#xa;&#xa;$content = $this-&gt;getContent();&#xa;$phpCode = &apos;&quot;&lt;\?php(.|[&apos; . PHP_EOL . &apos;])*?\?&gt;&quot;&apos;;&#xa;$reorganizedCode = array();&#xa;&#xa;if ( preg_match( $phpCode, $content, $matches ) ) {&#xa;    foreach ( $matches[1] as $code ) {&#xa;         $reorganizedCode[] = &apos;&lt;?php&apos; . PHP_EOL . $this-&gt;reformat( $code ) . PHP_EOL . &apos;?&gt;&apos;;&#xa;    }&#xa;    &#xa;    $i = 0;&#xa;    &#xa;    foreach ( $matches[0] as $code ) {&#xa;        $content = str_replace( $code, $reorganizedCode[$i++], $content );&#xa;    }&#xa;    &#xa;    $this-&gt;setContent( $content );&#xa;}" VSHIFT="2">
<edge COLOR="#808080"/>
</node>
</node>
